import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';
import { postsAPI } from '../services/api';
import toast from 'react-hot-toast';

const UserDashboard = () => {
  const { user } = useAuth();
  const [posts, setPosts] = useState([]);
  const [stats, setStats] = useState({ total: 0, published: 0, draft: 0 });
  const [loading, setLoading] = useState(true);
  const [showModal, setShowModal] = useState(false);
  const [editingPost, setEditingPost] = useState(null);
  const [formData, setFormData] = useState({ title: '', content: '', status: 'published' });

  useEffect(() => { fetchData(); }, []);

  const fetchData = async () => {
    try {
      const [postsRes, statsRes] = await Promise.all([
        postsAPI.getAll({ limit: 10 }),
        postsAPI.getStats()
      ]);
      setPosts(postsRes.data.data);
      setStats(statsRes.data.data);
    } catch (error) {
      console.error(error);
    } finally {
      setLoading(false);
    }
  };

  const openModal = (post = null) => {
    if (post) {
      setEditingPost(post);
      setFormData({ title: post.title, content: post.content, status: post.status });
    } else {
      setEditingPost(null);
      setFormData({ title: '', content: '', status: 'published' });
    }
    setShowModal(true);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (editingPost) {
        await postsAPI.update(editingPost._id, formData);
        toast.success('Post updated!');
      } else {
        await postsAPI.create(formData);
        toast.success('Post created!');
      }
      setShowModal(false);
      fetchData();
    } catch (error) {
      toast.error(error.response?.data?.message || 'Error saving post');
    }
  };

  const handleDelete = async (id) => {
    if (window.confirm('Delete this post?')) {
      try {
        await postsAPI.delete(id);
        toast.success('Post deleted!');
        fetchData();
      } catch (error) {
        toast.error('Error deleting post');
      }
    }
  };

  if (loading) return <div className="loading"><div className="spinner"></div></div>;

  return (
    <div>
      <div className="dashboard-header">
        <h1>My Dashboard</h1>
        <p>Welcome, {user.name}! Manage your posts here.</p>
      </div>

      <div className="stats-grid">
        <div className="stat-card">
          <div className="stat-icon primary">üìù</div>
          <div className="stat-content"><h3>{stats.total}</h3><p>Total Posts</p></div>
        </div>
        <div className="stat-card">
          <div className="stat-icon success">‚úÖ</div>
          <div className="stat-content"><h3>{stats.published}</h3><p>Published</p></div>
        </div>
        <div className="stat-card">
          <div className="stat-icon warning">üìã</div>
          <div className="stat-content"><h3>{stats.draft}</h3><p>Drafts</p></div>
        </div>
      </div>

      <div className="card">
        <div className="card-header">
          <h2>My Posts</h2>
          <button onClick={() => openModal()} className="btn btn-primary">+ New Post</button>
        </div>
        <div className="card-body">
          {posts.length === 0 ? (
            <div className="empty-state">
              <div className="empty-state-icon">üìù</div>
              <h3>No posts yet</h3>
              <p>Create your first post!</p>
            </div>
          ) : (
            <div className="posts-grid">
              {posts.map(post => (
                <div key={post._id} className="post-card">
                  <h3>{post.title}</h3>
                  <div className="post-meta">
                    {new Date(post.createdAt).toLocaleDateString()} ‚Ä¢ {post.views} views
                  </div>
                  <p className="post-content">{post.content.substring(0, 150)}...</p>
                  <div className="post-footer">
                    <span className={`badge badge-${post.status}`}>{post.status}</span>
                    <div className="actions">
                      <button onClick={() => openModal(post)} className="btn btn-sm btn-secondary">Edit</button>
                      <button onClick={() => handleDelete(post._id)} className="btn btn-sm btn-danger">Delete</button>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>

      {showModal && (
        <div className="modal-overlay" onClick={() => setShowModal(false)}>
          <div className="modal" onClick={e => e.stopPropagation()}>
            <div className="modal-header">
              <h2>{editingPost ? 'Edit Post' : 'New Post'}</h2>
              <button className="modal-close" onClick={() => setShowModal(false)}>√ó</button>
            </div>
            <div className="modal-body">
              <form onSubmit={handleSubmit}>
                <div className="form-group">
                  <label>Title</label>
                  <input type="text" value={formData.title} onChange={e => setFormData({...formData, title: e.target.value})} required />
                </div>
                <div className="form-group">
                  <label>Content</label>
                  <textarea value={formData.content} onChange={e => setFormData({...formData, content: e.target.value})} required />
                </div>
                <div className="form-group">
                  <label>Status</label>
                  <select value={formData.status} onChange={e => setFormData({...formData, status: e.target.value})}>
                    <option value="draft">Draft</option>
                    <option value="published">Published</option>
                  </select>
                </div>
                <div className="modal-footer" style={{padding: 0, border: 'none'}}>
                  <button type="button" className="btn btn-secondary" onClick={() => setShowModal(false)}>Cancel</button>
                  <button type="submit" className="btn btn-primary">{editingPost ? 'Update' : 'Create'}</button>
                </div>
              </form>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default UserDashboard;
